// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IGameToken {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
}

contract FarmGame {
    IGameToken public token;

    struct Seed {
        string name;
        uint256 price;
        uint256 growthTime;
        uint256 baseYield;
    }

    struct Plant {
        uint256 seedId;
        uint256 plantedAt;
        uint256 harvestTime;
        bool harvested;
    }

    struct Player {
        uint256 level;
        uint256 experience;
        uint256 plotCount;
        bool initialized;
    }

    mapping(uint256 => Seed) public seeds;
    uint256 public seedCount;

    mapping(address => Player) public players;
    mapping(address => mapping(uint256 => Plant)) public plots;
    mapping(address => mapping(uint256 => uint256)) public inventory;

    uint256 public constant INITIAL_PLOTS = 3;
    uint256 public constant PLOT_PRICE = 100 * 10**18;
    uint256 public constant MAX_PLOTS = 20;

    event PlayerInitialized(address player);
    event SeedPurchased(address player, uint256 seedId, uint256 quantity);
    event PlantPlanted(address player, uint256 plotId, uint256 seedId);
    event PlantHarvested(address player, uint256 plotId, uint256 reward);
    event LevelUp(address player, uint256 newLevel);
    event PlotPurchased(address player, uint256 newPlotId);

    constructor(address _token) {
        token = IGameToken(_token);

        // Инициализация семян
        seeds[0] = Seed("Wheat", 10 * 10**18, 1 minutes, 20);
        seeds[1] = Seed("Corn", 20 * 10**18, 2 minutes, 50);
        seeds[2] = Seed("Tomato", 30 * 10**18, 3 minutes, 80);
        seeds[3] = Seed("Pumpkin", 50 * 10**18, 5 minutes, 150);
        seedCount = 4;
    }

    function initializePlayer() external {
        require(!players[msg.sender].initialized, "Already initialized");

        players[msg.sender] = Player({
            level: 1,
            experience: 0,
            plotCount: INITIAL_PLOTS,
            initialized: true
        });

        // Начальные семена
        inventory[msg.sender][0] = 5;

        emit PlayerInitialized(msg.sender);
    }

    function buySeed(uint256 _seedId, uint256 _quantity) external {
        require(_seedId < seedCount, "Invalid seed");
        require(players[msg.sender].initialized, "Not initialized");

        uint256 cost = seeds[_seedId].price * _quantity;
        require(token.transferFrom(msg.sender, address(this), cost), "Payment failed");

        inventory[msg.sender][_seedId] += _quantity;
        emit SeedPurchased(msg.sender, _seedId, _quantity);
    }

    function plantSeed(uint256 _plotId, uint256 _seedId) external {
        require(players[msg.sender].initialized, "Not initialized");
        require(_plotId < players[msg.sender].plotCount, "Invalid plot");
        require(plots[msg.sender][_plotId].harvestTime == 0 || 
                plots[msg.sender][_plotId].harvested, "Plot occupied");
        require(inventory[msg.sender][_seedId] > 0, "No seeds");

        inventory[msg.sender][_seedId]--;

        plots[msg.sender][_plotId] = Plant({
            seedId: _seedId,
            plantedAt: block.timestamp,
            harvestTime: block.timestamp + seeds[_seedId].growthTime,
            harvested: false
        });

        emit PlantPlanted(msg.sender, _plotId, _seedId);
    }

    function harvest(uint256 _plotId) external {
        Plant storage plant = plots[msg.sender][_plotId];
        require(!plant.harvested, "Already harvested");
        require(block.timestamp >= plant.harvestTime, "Not ready");

        plant.harvested = true;

        // Расчет награды с учетом уровня
        uint256 baseReward = seeds[plant.seedId].baseYield * 10**18;
        uint256 levelBonus = (baseReward * players[msg.sender].level) / 10;
        uint256 totalReward = baseReward + levelBonus;

        // Опыт
        uint256 expGain = seeds[plant.seedId].baseYield;
        players[msg.sender].experience += expGain;

        // Проверка повышения уровня
        uint256 newLevel = (players[msg.sender].experience / 100) + 1;
        if (newLevel > players[msg.sender].level) {
            players[msg.sender].level = newLevel;
            emit LevelUp(msg.sender, newLevel);
        }

        require(token.transfer(msg.sender, totalReward), "Reward transfer failed");
        emit PlantHarvested(msg.sender, _plotId, totalReward);
    }

    function buyPlot() external {
        require(players[msg.sender].initialized, "Not initialized");
        require(players[msg.sender].plotCount < MAX_PLOTS, "Max plots reached");

        require(token.transferFrom(msg.sender, address(this), PLOT_PRICE), "Payment failed");

        uint256 newPlotId = players[msg.sender].plotCount;
        players[msg.sender].plotCount++;

        emit PlotPurchased(msg.sender, newPlotId);
    }

    function getPlayerInfo(address _player) external view returns (
        uint256 level,
        uint256 experience,
        uint256 plotCount,
        bool initialized
    ) {
        Player memory p = players[_player];
        return (p.level, p.experience, p.plotCount, p.initialized);
    }

    function getPlotInfo(address _player, uint256 _plotId) external view returns (
        uint256 seedId,
        uint256 plantedAt,
        uint256 harvestTime,
        bool harvested,
        bool canHarvest,
        uint256 progress
    ) {
        Plant memory p = plots[_player][_plotId];

        if (p.harvestTime == 0) {
            return (0, 0, 0, false, false, 0);
        }

        bool ready = block.timestamp >= p.harvestTime && !p.harvested;
        uint256 prog = 100;

        if (!p.harvested && block.timestamp < p.harvestTime) {
            uint256 elapsed = block.timestamp - p.plantedAt;
            uint256 total = p.harvestTime - p.plantedAt;
            prog = (elapsed * 100) / total;
        }

        return (p.seedId, p.plantedAt, p.harvestTime, p.harvested, ready, prog);
    }

    function getInventory(address _player, uint256 _seedId) external view returns (uint256) {
        return inventory[_player][_seedId];
    }
}

