pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title FarmLand
 * @notice ERC721 NFT representing land plots (8x6 grid = 48 plots)
 */
contract FarmLand is ERC721, Ownable {
    uint256 public constant MAX_PLOTS = 48; // 8x6 grid
    uint256 private _nextTokenId;
    
    struct LandPlot {
        uint8 x;           // 0-7
        uint8 y;           // 0-5
        uint16 fertility;  // 0-1000
        uint256 ownedSince;
    }
    
    mapping(uint256 => LandPlot) public landPlots;
    mapping(address => bool) public authorizedManagers;
    
    event LandMinted(uint256 indexed tokenId, address indexed owner, uint8 x, uint8 y);
    event FertilityChanged(uint256 indexed tokenId, uint16 newFertility);
    event ManagerAuthorized(address indexed manager, bool authorized);
    
    constructor(address initialOwner) 
        ERC721("Farm Land", "LAND") 
        Ownable(initialOwner)
    {}
    
    modifier onlyAuthorized() {
        require(authorizedManagers[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }
    
    /**
     * @notice Authorize a contract to manage land
     */
    function setAuthorizedManager(address manager, bool authorized) external onlyOwner {
        authorizedManagers[manager] = authorized;
        emit ManagerAuthorized(manager, authorized);
    }
    
    /**
     * @notice Mint a land plot NFT
     */
    function mint(address to, uint8 x, uint8 y) external onlyAuthorized returns (uint256) {
        require(_nextTokenId < MAX_PLOTS, "All plots minted");
        require(x < 8 && y < 6, "Invalid coordinates");
        
        uint256 tokenId = _nextTokenId++;
        _safeMint(to, tokenId);
        
        landPlots[tokenId] = LandPlot({
            x: x,
            y: y,
            fertility: 1000,
            ownedSince: block.timestamp
        });
        
        emit LandMinted(tokenId, to, x, y);
        return tokenId;
    }
    
    /**
     * @notice Update land fertility (affected by events)
     */
    function updateFertility(uint256 tokenId, uint16 newFertility) external onlyAuthorized {
        require(newFertility <= 1000, "Fertility exceeds max");
        landPlots[tokenId].fertility = newFertility;
        emit FertilityChanged(tokenId, newFertility);
    }
    
    /**
     * @notice Get land plot info
     */
    function getLandPlot(uint256 tokenId) external view returns (LandPlot memory) {
        return landPlots[tokenId];
    }
    
    /**
     * @notice Get all land plots owned by address
     */
    function getLandsByOwner(address owner) external view returns (uint256[] memory) {
        uint256 balance = balanceOf(owner);
        uint256[] memory ownedTokenIds = new uint256[](balance);
        
        uint256 currentIndex = 0;
        for (uint256 i = 0; i < _nextTokenId; i++) {
            if (_ownerOf(i) == owner) {
                ownedTokenIds[currentIndex] = i;
                currentIndex++;
            }
        }
        
        return ownedTokenIds;
    }
}
","keccak256":"0x55a968d50d9b609619cf1ba66c4cbe47629e7918414e132dccdd9ef987a37d12","license":"MIT"},"contracts/FarmMarketplace.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./FarmCoin.sol";
import "./PlantNFT.sol";
import "./FarmLand.sol";
import "./GameEvents.sol";
import "./GameEffects.sol";
import "./PriceOracle.sol";

/**
 * @title FarmMarketplace
 * @notice Main marketplace for buying seeds, planting, harvesting, and selling crops
 * @dev Orchestrates the entire chain reaction: Buy -> Plant -> Event -> Effect -> Price Change
 */
contract FarmMarketplace is Ownable, ReentrancyGuard {
    FarmCoin public farmCoin;
    PlantNFT public plantNFT;
    FarmLand public farmLand;
    GameEvents public gameEvents;
    GameEffects public gameEffects;
    PriceOracle public priceOracle;
    
    // Shop inventory
    mapping(PriceOracle.SeedType => uint256) public seedInventory;
    
    event SeedPurchased(address indexed buyer, PriceOracle.SeedType seedType, uint256 quantity, uint256 totalCost);
    event PlantPlanted(address indexed farmer, uint256 plantTokenId, uint256 landTokenId);
    event CropHarvested(address indexed farmer, uint256 plantTokenId, uint256 reward);
    event CropSold(address indexed seller, PlantNFT.PlantType cropType, uint256 quantity, uint256 totalEarned);
    event EventTriggeredAndApplied(uint256 indexed eventId, address indexed farmer);
    
    constructor(
        address initialOwner,
        address _farmCoin,
        address _plantNFT,
        address _farmLand,
        address _gameEvents,
        address _gameEffects,
        address _priceOracle
    ) Ownable(initialOwner) {
        farmCoin = FarmCoin(_farmCoin);
        plantNFT = PlantNFT(_plantNFT);
        farmLand = FarmLand(_farmLand);
        gameEvents = GameEvents(_gameEvents);
        gameEffects = GameEffects(_gameEffects);
        priceOracle = PriceOracle(_priceOracle);
        
        // Initialize shop inventory (unlimited for demo)
        seedInventory[PriceOracle.SeedType.WHEAT_SEED] = type(uint256).max;
        seedInventory[PriceOracle.SeedType.GRAPE_SEED] = type(uint256).max;
        seedInventory[PriceOracle.SeedType.PUMPKIN_SEED] = type(uint256).max;
    }
    
    /**
     * @notice Buy seeds from shop using FarmCoin
     */
    function buySeed(PriceOracle.SeedType seedType, uint256 quantity) external nonReentrant {
        require(quantity > 0, "Invalid quantity");
        require(seedInventory[seedType] >= quantity, "Insufficient inventory");
        
        uint256 pricePerSeed = priceOracle.getSeedPrice(seedType);
        uint256 totalCost = pricePerSeed * quantity;
        
        // Burn FarmCoin from buyer
        farmCoin.burnFrom(msg.sender, totalCost);
        
        // Update inventory
        seedInventory[seedType] -= quantity;
        
        emit SeedPurchased(msg.sender, seedType, quantity, totalCost);
        
        // TODO: Track seeds in user's inventory (off-chain or separate contract)
    }
    
    /**
     * @notice Plant a seed on owned land
     */
    function plantSeed(
        PriceOracle.SeedType seedType,
        uint256 landTokenId
    ) external nonReentrant returns (uint256 plantTokenId) {
        require(farmLand.ownerOf(landTokenId) == msg.sender, "Not land owner");
        
        // Convert seed type to plant type
        PlantNFT.PlantType plantType;
        if (seedType == PriceOracle.SeedType.WHEAT_SEED) {
            plantType = PlantNFT.PlantType.WHEAT;
        } else if (seedType == PriceOracle.SeedType.GRAPE_SEED) {
            plantType = PlantNFT.PlantType.GRAPE;
        } else {
            plantType = PlantNFT.PlantType.PUMPKIN;
        }
        
        // Mint plant NFT
        plantTokenId = plantNFT.mint(msg.sender, plantType, landTokenId);
        
        emit PlantPlanted(msg.sender, plantTokenId, landTokenId);
        
        return plantTokenId;
    }
    
    /**
     * @notice Harvest a mature plant
     */
    function harvestCrop(uint256 plantTokenId) external nonReentrant {
        require(plantNFT.ownerOf(plantTokenId) == msg.sender, "Not plant owner");
        require(plantNFT.isReadyToHarvest(plantTokenId), "Plant not ready");
        
        PlantNFT.Plant memory plant = plantNFT.getPlant(plantTokenId);
        
        // Calculate reward based on health
        PriceOracle.CropType cropType;
        if (plant.plantType == PlantNFT.PlantType.WHEAT) {
            cropType = PriceOracle.CropType.WHEAT;
        } else if (plant.plantType == PlantNFT.PlantType.GRAPE) {
            cropType = PriceOracle.CropType.GRAPE;
        } else {
            cropType = PriceOracle.CropType.PUMPKIN;
        }
        
        uint256 baseReward = priceOracle.getCropPrice(cropType);
        uint256 reward = (baseReward * plant.health) / 1000; // Scale by health
        
        // Mint FarmCoin reward
        farmCoin.mint(msg.sender, reward);
        
        // Burn plant NFT
        plantNFT.burn(plantTokenId);
        
        emit CropHarvested(msg.sender, plantTokenId, reward);
    }
    
    /**
     * @notice Trigger random event and apply effects (CHAIN REACTION STARTS HERE!)
     * @dev Anyone can trigger events, but effects apply to their own farm
     */
    function triggerRandomEvent() external nonReentrant {
        // 1️⃣ STEP 1: Generate random event
        uint256 eventId = gameEvents.triggerEvent();
        
        // 2️⃣ STEP 2: Apply effects to caller's farm
        gameEffects.applyEffects(eventId, msg.sender);
        
        // 3️⃣ STEP 3: Update market prices
        priceOracle.updatePrices(eventId);
        
        emit EventTriggeredAndApplied(eventId, msg.sender);
    }
    
    /**
     * @notice Mint initial land plots to a new player
     */
    function mintStarterLand(address player) external onlyOwner {
        // Mint 8x6 grid (48 plots)
        for (uint8 y = 0; y < 6; y++) {
            for (uint8 x = 0; x < 8; x++) {
                farmLand.mint(player, x, y);
            }
        }
    }
    
    /**
     * @notice Give starter coins to new players
     */
    function giveStarterCoins(address player, uint256 amount) external onlyOwner {
        farmCoin.mint(player, amount);
    }
    
    /**
     * @notice Emergency withdraw (owner only)
     */
    function emergencyWithdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
    
    /**
     * @notice Get player's farm status
     */
    function getPlayerStatus(address player) external view returns (
        uint256 coinBalance,
        uint256 landCount,
        uint256[] memory landTokenIds
    ) {
        coinBalance = farmCoin.balanceOf(player);
        landTokenIds = farmLand.getLandsByOwner(player);
        landCount = landTokenIds.length;
        
        return (coinBalance, landCount, landTokenIds);
    }
    
    receive() external payable {}
}
","keccak256":"0x45c1e90d9398d2cebd71b5302dcaa4b0c145f4010f9fa718cf84db91e5df2329","license":"MIT"},"contracts/GameEffects.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./GameEvents.sol";
import "./PlantNFT.sol";
import "./FarmLand.sol";

/**
 * @title GameEffects
 * @notice Applies event effects to plants and land
 * @dev Second contract in the chain reaction (called after GameEvents)
 */
contract GameEffects is Ownable {
    PlantNFT public plantNFT;
    FarmLand public farmLand;
    GameEvents public gameEvents;
    
    // Next contract in chain (PriceOracle)
    address public priceOracle;
    
    struct EffectResult {
        int16 healthChange;     // -1000 to +1000
        int16 fertilityChange;  // -1000 to +1000
        uint16 affectedPlants;
        uint16 affectedLand;
    }
    
    event EffectsApplied(
        uint256 indexed eventId,
        int16 avgHealthChange,
        int16 avgFertilityChange,
        uint16 affectedPlants,
        uint16 affectedLand
    );
    
    constructor(
        address initialOwner,
        address _plantNFT,
        address _farmLand,
        address _gameEvents
    ) Ownable(initialOwner) {
        plantNFT = PlantNFT(_plantNFT);
        farmLand = FarmLand(_farmLand);
        gameEvents = GameEvents(_gameEvents);
    }
    
    /**
     * @notice Set price oracle contract
     */
    function setPriceOracle(address _priceOracle) external onlyOwner {
        priceOracle = _priceOracle;
    }
    
    /**
     * @notice Apply effects to a player's farm based on event
     * @dev Called automatically after event is triggered
     */
    function applyEffects(
        uint256 eventId,
        address farmer
    ) external returns (EffectResult memory) {
        GameEvents.GameEvent memory gameEvent = gameEvents.getEvent(eventId);
        
        // Get farmer's land plots
        uint256[] memory landTokenIds = farmLand.getLandsByOwner(farmer);
        
        EffectResult memory result = EffectResult({
            healthChange: 0,
            fertilityChange: 0,
            affectedPlants: 0,
            affectedLand: 0
        });
        
        // Apply effects based on event type
        (int16 healthMod, int16 fertilityMod) = _calculateModifiers(
            gameEvent.eventType,
            gameEvent.severity
        );
        
        // Apply to all farmer's land plots
        for (uint256 i = 0; i < landTokenIds.length; i++) {
            uint256 landId = landTokenIds[i];
            FarmLand.LandPlot memory plot = farmLand.getLandPlot(landId);
            
            // Update land fertility
            uint16 newFertility = _applyChange(plot.fertility, fertilityMod);
            farmLand.updateFertility(landId, newFertility);
            result.affectedLand++;
            result.fertilityChange += fertilityMod;
        }
        
        // TODO: Apply to plants on these land plots
        // For now, simplified version without tracking plant-land mapping
        
        emit EffectsApplied(
            eventId,
            healthMod,
            fertilityMod,
            result.affectedPlants,
            result.affectedLand
        );
        
        return result;
    }
    
    /**
     * @notice Apply effect to a specific plant
     */
    function applyEffectToPlant(uint256 plantTokenId, uint256 eventId) external {
        require(plantNFT.ownerOf(plantTokenId) == msg.sender, "Not plant owner");
        
        GameEvents.GameEvent memory gameEvent = gameEvents.getEvent(eventId);
        PlantNFT.Plant memory plant = plantNFT.getPlant(plantTokenId);
        
        (int16 healthMod, ) = _calculateModifiers(
            gameEvent.eventType,
            gameEvent.severity
        );
        
        // Apply health change
        uint16 newHealth = _applyChange(plant.health, healthMod);
        plantNFT.updateHealth(plantTokenId, newHealth);
    }
    
    /**
     * @notice Calculate modifiers based on event type and severity
     */
    function _calculateModifiers(
        GameEvents.EventType eventType,
        uint16 severity
    ) internal pure returns (int16 healthMod, int16 fertilityMod) {
        if (eventType == GameEvents.EventType.NONE) {
            return (0, 0);
        } else if (eventType == GameEvents.EventType.RAIN) {
            // Positive effect
            healthMod = int16(uint16(severity / 5));  // +0 to +200
            fertilityMod = int16(uint16(severity / 10)); // +0 to +100
        } else if (eventType == GameEvents.EventType.SUNSTORM) {
            // Very positive
            healthMod = int16(uint16(severity / 3));  // +0 to +333
            fertilityMod = int16(uint16(severity / 5)); // +0 to +200
        } else if (eventType == GameEvents.EventType.LOCUSTS) {
            // Heavy damage
            healthMod = -int16(uint16(severity / 2));  // -0 to -500
            fertilityMod = -int16(uint16(severity / 4)); // -0 to -250
        } else if (eventType == GameEvents.EventType.FROST) {
            // Very heavy damage
            healthMod = -int16(uint16((severity * 7) / 10));  // -0 to -700
            fertilityMod = -int16(uint16(severity / 3)); // -0 to -333
        } else if (eventType == GameEvents.EventType.DROUGHT) {
            // Moderate damage
            healthMod = -int16(uint16(severity / 3));  // -0 to -333
            fertilityMod = -int16(uint16(severity / 5)); // -0 to -200
        } else if (eventType == GameEvents.EventType.WIND) {
            // Minor damage
            healthMod = -int16(uint16(severity / 5));  // -0 to -200
            fertilityMod = -int16(uint16(severity / 10)); // -0 to -100
        } else if (eventType == GameEvents.EventType.PESTS) {
            // Moderate damage
            healthMod = -int16(uint16(severity / 4));  // -0 to -250
            fertilityMod = -int16(uint16(severity / 6)); // -0 to -166
        }
        
        return (healthMod, fertilityMod);
    }
    
    /**
     * @notice Apply change to a value (0-1000 range)
     */
    function _applyChange(uint16 current, int16 change) internal pure returns (uint16) {
        int32 result = int32(int16(current)) + int32(change);
        if (result < 0) return 0;
        if (result > 1000) return 1000;
        return uint16(uint32(result));
    }
}
