pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title GameEvents
 * @notice Generates random farm events (locusts, wind, rain, etc.)
 * @dev First contract in the chain reaction
 */
contract GameEvents is Ownable {
    enum EventType {
        NONE,       // 30% - Nothing happens
        LOCUSTS,    // 15% - Damage crops
        WIND,       // 10% - Minor damage
        RAIN,       // 20% - Good for growth
        DROUGHT,    // 10% - Reduces health
        FROST,      // 5%  - Heavy damage
        SUNSTORM,   // 5%  - Boosts growth
        PESTS       // 5%  - Moderate damage
    }
    
    struct GameEvent {
        EventType eventType;
        uint16 severity;      // 0-1000
        uint256 timestamp;
        uint256 blockNumber;
    }
    
    // Store event history
    GameEvent[] public eventHistory;
    
    // Next contract in chain (FarmEffects)
    address public effectsContract;
    
    event EventTriggered(
        uint256 indexed eventId,
        EventType eventType,
        uint16 severity,
        uint256 timestamp
    );
    
    constructor(address initialOwner) Ownable(initialOwner) {}
    
    /**
     * @notice Set the effects contract address
     */
    function setEffectsContract(address _effectsContract) external onlyOwner {
        effectsContract = _effectsContract;
    }
    
    /**
     * @notice Generate a random event (called by keeper or player action)
     */
    function triggerEvent() external returns (uint256 eventId) {
        // Generate pseudo-random number
        uint256 random = uint256(
            keccak256(
                abi.encodePacked(
                    block.timestamp,
                    block.prevrandao,
                    msg.sender,
                    eventHistory.length
                )
            )
        );
        
        // Select event type based on probability
        EventType eventType = _selectEventType(random % 100);
        
        // Calculate severity (0-1000)
        uint16 severity = uint16((random >> 8) % 1001);
        
        // Store event
        GameEvent memory newEvent = GameEvent({
            eventType: eventType,
            severity: severity,
            timestamp: block.timestamp,
            blockNumber: block.number
        });
        
        eventHistory.push(newEvent);
        eventId = eventHistory.length - 1;
        
        emit EventTriggered(eventId, eventType, severity, block.timestamp);
        
        return eventId;
    }
    
    /**
     * @notice Select event type based on weighted probability
     */
    function _selectEventType(uint256 rand) internal pure returns (EventType) {
        if (rand < 30) return EventType.NONE;        // 30%
        if (rand < 50) return EventType.RAIN;        // 20%
        if (rand < 65) return EventType.LOCUSTS;     // 15%
        if (rand < 75) return EventType.WIND;        // 10%
        if (rand < 85) return EventType.DROUGHT;     // 10%
        if (rand < 90) return EventType.SUNSTORM;    // 5%
        if (rand < 95) return EventType.FROST;       // 5%
        return EventType.PESTS;                      // 5%
    }
    
    /**
     * @notice Get event by ID
     */
    function getEvent(uint256 eventId) external view returns (GameEvent memory) {
        require(eventId < eventHistory.length, "Event does not exist");
        return eventHistory[eventId];
    }
    
    /**
     * @notice Get total events count
     */
    function getEventsCount() external view returns (uint256) {
        return eventHistory.length;
    }
    
    /**
     * @notice Get recent events
     */
    function getRecentEvents(uint256 count) external view returns (GameEvent[] memory) {
        uint256 total = eventHistory.length;
        if (count > total) count = total;
        
        GameEvent[] memory recent = new GameEvent[](count);
        for (uint256 i = 0; i < count; i++) {
            recent[i] = eventHistory[total - count + i];
        }
        
        return recent;
    }
}
","keccak256":"0x6248c77448eb8c8bcc42f022ba425eea3db47ea4da0269e89247dafc3c61d126","license":"MIT"},"contracts/PlantNFT.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title PlantNFT
 * @notice ERC721 NFT representing planted crops
 * @dev Each plant has type, growth stage, health, and planted timestamp
 */
contract PlantNFT is ERC721, Ownable {
    uint256 private _nextTokenId;
    
    enum PlantType { WHEAT, GRAPE, PUMPKIN }
    
    struct Plant {
        PlantType plantType;
        uint8 growthStage;      // 0-3 (seed -> mature)
        uint16 health;          // 0-1000
        uint256 plantedAt;      // timestamp
        uint256 landTokenId;    // which land plot it's on
    }
    
    mapping(uint256 => Plant) public plants;
    mapping(address => bool) public authorizedManagers;
    
    event PlantCreated(uint256 indexed tokenId, address indexed owner, PlantType plantType, uint256 landTokenId);
    event PlantGrown(uint256 indexed tokenId, uint8 newStage);
    event PlantHealthChanged(uint256 indexed tokenId, uint16 newHealth);
    event ManagerAuthorized(address indexed manager, bool authorized);
    
    constructor(address initialOwner) 
        ERC721("Farm Plant", "PLANT") 
        Ownable(initialOwner)
    {}
    
    modifier onlyAuthorized() {
        require(authorizedManagers[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }
    
    /**
     * @notice Authorize a contract to manage plants
     */
    function setAuthorizedManager(address manager, bool authorized) external onlyOwner {
        authorizedManagers[manager] = authorized;
        emit ManagerAuthorized(manager, authorized);
    }
    
    /**
     * @notice Mint a new plant NFT
     */
    function mint(
        address to,
        PlantType plantType,
        uint256 landTokenId
    ) external onlyAuthorized returns (uint256) {
        uint256 tokenId = _nextTokenId++;
        _safeMint(to, tokenId);
        
        plants[tokenId] = Plant({
            plantType: plantType,
            growthStage: 0,
            health: 1000,
            plantedAt: block.timestamp,
            landTokenId: landTokenId
        });
        
        emit PlantCreated(tokenId, to, plantType, landTokenId);
        return tokenId;
    }
    
    /**
     * @notice Update plant growth stage
     */
    function updateGrowthStage(uint256 tokenId, uint8 newStage) external onlyAuthorized {
        require(newStage <= 3, "Invalid growth stage");
        plants[tokenId].growthStage = newStage;
        emit PlantGrown(tokenId, newStage);
    }
    
    /**
     * @notice Update plant health (called by FarmEffects contract)
     */
    function updateHealth(uint256 tokenId, uint16 newHealth) external onlyAuthorized {
        require(newHealth <= 1000, "Health exceeds max");
        plants[tokenId].health = newHealth;
        emit PlantHealthChanged(tokenId, newHealth);
    }
    
    /**
     * @notice Burn plant NFT (when harvested)
     */
    function burn(uint256 tokenId) external onlyAuthorized {
        _burn(tokenId);
        delete plants[tokenId];
    }
    
    /**
     * @notice Get plant info
     */
    function getPlant(uint256 tokenId) external view returns (Plant memory) {
        return plants[tokenId];
    }
    
    /**
     * @notice Check if plant is ready to harvest
     */
    function isReadyToHarvest(uint256 tokenId) external view returns (bool) {
        Plant memory plant = plants[tokenId];
        
        // Growth time in seconds
        uint256 requiredTime;
        if (plant.plantType == PlantType.WHEAT) {
            requiredTime = 15; // 15 seconds for demo
        } else if (plant.plantType == PlantType.GRAPE) {
            requiredTime = 25;
        } else {
            requiredTime = 35;
        }
        
        return (block.timestamp >= plant.plantedAt + requiredTime) && 
               (plant.growthStage >= 3) &&
               (plant.health > 0);
    }
}
","keccak256":"0x35779688e4c48210f3094815003017357305f9f59898b704baa8b8d6c06c8b66","license":"MIT"},"contracts/PriceOracle.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./GameEvents.sol";
import "./GameEffects.sol";

/**
 * @title PriceOracle
 * @notice Dynamic pricing based on events and effects
 * @dev Third contract in the chain reaction
 */
contract PriceOracle is Ownable {
    GameEvents public gameEvents;
    GameEffects public gameEffects;
    
    enum CropType { WHEAT, GRAPE, PUMPKIN }
    enum SeedType { WHEAT_SEED, GRAPE_SEED, PUMPKIN_SEED }
    
    // Base prices in wei (for demo, small values)
    uint256 public constant BASE_WHEAT_SEED = 0.005 ether;
    uint256 public constant BASE_GRAPE_SEED = 0.012 ether;
    uint256 public constant BASE_PUMPKIN_SEED = 0.020 ether;
    
    uint256 public constant BASE_WHEAT_CROP = 0.010 ether;
    uint256 public constant BASE_GRAPE_CROP = 0.025 ether;
    uint256 public constant BASE_PUMPKIN_CROP = 0.045 ether;
    
    // Current price multipliers (1000 = 1.0x, 1500 = 1.5x)
    mapping(CropType => uint256) public cropMultipliers;
    mapping(SeedType => uint256) public seedMultipliers;
    
    // Price history
    struct PriceSnapshot {
        uint256 timestamp;
        uint256 wheatPrice;
        uint256 grapePrice;
        uint256 pumpkinPrice;
    }
    
    PriceSnapshot[] public priceHistory;
    
    event PricesUpdated(
        uint256 indexed eventId,
        uint256 wheatMultiplier,
        uint256 grapeMultiplier,
        uint256 pumpkinMultiplier
    );
    
    constructor(
        address initialOwner,
        address _gameEvents,
        address _gameEffects
    ) Ownable(initialOwner) {
        gameEvents = GameEvents(_gameEvents);
        gameEffects = GameEffects(_gameEffects);
        
        // Initialize at 1.0x
        cropMultipliers[CropType.WHEAT] = 1000;
        cropMultipliers[CropType.GRAPE] = 1000;
        cropMultipliers[CropType.PUMPKIN] = 1000;
        
        seedMultipliers[SeedType.WHEAT_SEED] = 1000;
        seedMultipliers[SeedType.GRAPE_SEED] = 1000;
        seedMultipliers[SeedType.PUMPKIN_SEED] = 1000;
    }
    
    /**
     * @notice Update prices based on recent event
     * @dev Called after effects are applied
     */
    function updatePrices(uint256 eventId) external {
        GameEvents.GameEvent memory gameEvent = gameEvents.getEvent(eventId);
        
        // Calculate price changes based on event type
        (int16 wheatChange, int16 grapeChange, int16 pumpkinChange) = 
            _calculatePriceChanges(gameEvent.eventType, gameEvent.severity);
        
        // Apply changes to multipliers
        cropMultipliers[CropType.WHEAT] = _applyMultiplierChange(
            cropMultipliers[CropType.WHEAT],
            wheatChange
        );
        cropMultipliers[CropType.GRAPE] = _applyMultiplierChange(
            cropMultipliers[CropType.GRAPE],
            grapeChange
        );
        cropMultipliers[CropType.PUMPKIN] = _applyMultiplierChange(
            cropMultipliers[CropType.PUMPKIN],
            pumpkinChange
        );
        
        // Seeds follow crop prices (inverse relationship)
        seedMultipliers[SeedType.WHEAT_SEED] = cropMultipliers[CropType.WHEAT];
        seedMultipliers[SeedType.GRAPE_SEED] = cropMultipliers[CropType.GRAPE];
        seedMultipliers[SeedType.PUMPKIN_SEED] = cropMultipliers[CropType.PUMPKIN];
        
        // Store price snapshot
        priceHistory.push(PriceSnapshot({
            timestamp: block.timestamp,
            wheatPrice: getCropPrice(CropType.WHEAT),
            grapePrice: getCropPrice(CropType.GRAPE),
            pumpkinPrice: getCropPrice(CropType.PUMPKIN)
        }));
        
        emit PricesUpdated(
            eventId,
            cropMultipliers[CropType.WHEAT],
            cropMultipliers[CropType.GRAPE],
            cropMultipliers[CropType.PUMPKIN]
        );
    }
    
    /**
     * @notice Calculate price changes based on event
     * @dev Bad events increase prices (scarcity), good events decrease prices (abundance)
     */
    function _calculatePriceChanges(
        GameEvents.EventType eventType,
        uint16 severity
    ) internal pure returns (int16 wheat, int16 grape, int16 pumpkin) {
        if (eventType == GameEvents.EventType.NONE) {
            // Prices slowly normalize
            return (-5, -5, -5);
        } else if (eventType == GameEvents.EventType.RAIN) {
            // Good harvest -> lower prices
            wheat = -int16(uint16(severity / 20));  // -0 to -50
            grape = -int16(uint16(severity / 25));
            pumpkin = -int16(uint16(severity / 30));
        } else if (eventType == GameEvents.EventType.SUNSTORM) {
            // Excellent harvest -> much lower prices
            wheat = -int16(uint16(severity / 10));  // -0 to -100
            grape = -int16(uint16(severity / 15));
            pumpkin = -int16(uint16(severity / 20));
        } else if (eventType == GameEvents.EventType.LOCUSTS) {
            // Crop damage -> higher prices
            wheat = int16(uint16(severity / 5));    // +0 to +200
            grape = int16(uint16(severity / 7));
            pumpkin = int16(uint16(severity / 10));
        } else if (eventType == GameEvents.EventType.FROST) {
            // Heavy damage -> much higher prices
            wheat = int16(uint16(severity / 3));    // +0 to +333
            grape = int16(uint16(severity / 4));
            pumpkin = int16(uint16(severity / 5));
        } else if (eventType == GameEvents.EventType.DROUGHT) {
            // Moderate damage -> moderate price increase
            wheat = int16(uint16(severity / 6));    // +0 to +166
            grape = int16(uint16(severity / 8));
            pumpkin = int16(uint16(severity / 10));
        } else if (eventType == GameEvents.EventType.WIND || eventType == GameEvents.EventType.PESTS) {
            // Minor damage -> slight price increase
            wheat = int16(uint16(severity / 15));   // +0 to +66
            grape = int16(uint16(severity / 20));
            pumpkin = int16(uint16(severity / 25));
        }
        
        return (wheat, grape, pumpkin);
    }
    
    /**
     * @notice Apply multiplier change (clamped between 0.5x and 2.0x)
     */
    function _applyMultiplierChange(uint256 current, int16 change) internal pure returns (uint256) {
        int32 result = int32(int256(current)) + int32(change);
        if (result < 500) return 500;    // Min 0.5x
        if (result > 2000) return 2000;  // Max 2.0x
        return uint256(uint32(result));
    }
    
    /**
     * @notice Get current seed price
     */
    function getSeedPrice(SeedType seedType) public view returns (uint256) {
        uint256 basePrice;
        if (seedType == SeedType.WHEAT_SEED) basePrice = BASE_WHEAT_SEED;
        else if (seedType == SeedType.GRAPE_SEED) basePrice = BASE_GRAPE_SEED;
        else basePrice = BASE_PUMPKIN_SEED;
        
        return (basePrice * seedMultipliers[seedType]) / 1000;
    }
    
    /**
     * @notice Get current crop sell price
     */
    function getCropPrice(CropType cropType) public view returns (uint256) {
        uint256 basePrice;
        if (cropType == CropType.WHEAT) basePrice = BASE_WHEAT_CROP;
        else if (cropType == CropType.GRAPE) basePrice = BASE_GRAPE_CROP;
        else basePrice = BASE_PUMPKIN_CROP;
        
        return (basePrice * cropMultipliers[cropType]) / 1000;
    }
    
    /**
     * @notice Get all current prices
     */
    function getAllPrices() external view returns (
        uint256 wheatSeed,
        uint256 grapeSeed,
        uint256 pumpkinSeed,
        uint256 wheatCrop,
        uint256 grapeCrop,
        uint256 pumpkinCrop
    ) {
        return (
            getSeedPrice(SeedType.WHEAT_SEED),
            getSeedPrice(SeedType.GRAPE_SEED),
            getSeedPrice(SeedType.PUMPKIN_SEED),
            getCropPrice(CropType.WHEAT),
            getCropPrice(CropType.GRAPE),
            getCropPrice(CropType.PUMPKIN)
        );
    }
    
    /**
     * @notice Get price history
     */
    function getPriceHistory(uint256 count) external view returns (PriceSnapshot[] memory) {
        uint256 total = priceHistory.length;
        if (count > total) count = total;
        
        PriceSnapshot[] memory recent = new PriceSnapshot[](count);
        for (uint256 i = 0; i < count; i++) {
            recent[i] = priceHistory[total - count + i];
        }
        
        return recent;
    }
}
