// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title FarmLand
 * @notice ERC721 NFT representing land plots (8x6 grid = 48 plots)
 */
contract FarmLand is ERC721, Ownable {
    uint256 public constant MAX_PLOTS = 48; // 8x6 grid
    uint256 private _nextTokenId;

    struct LandPlot {
        uint8 x;           // 0-7
        uint8 y;           // 0-5
        uint16 fertility;  // 0-1000
        uint256 ownedSince;
    }

    mapping(uint256 => LandPlot) public landPlots;
    mapping(address => bool) public authorizedManagers;

    event LandMinted(uint256 indexed tokenId, address indexed owner, uint8 x, uint8 y);
    event FertilityChanged(uint256 indexed tokenId, uint16 newFertility);
    event ManagerAuthorized(address indexed manager, bool authorized);

    constructor(address initialOwner) 
        ERC721("Farm Land", "LAND") 
        Ownable(initialOwner)
    {}

    modifier onlyAuthorized() {
        require(authorizedManagers[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }

    /**
     * @notice Authorize a contract to manage land
     */
    function setAuthorizedManager(address manager, bool authorized) external onlyOwner {
        authorizedManagers[manager] = authorized;
        emit ManagerAuthorized(manager, authorized);
    }

    /**
     * @notice Mint a land plot NFT
     */
    function mint(address to, uint8 x, uint8 y) external onlyAuthorized returns (uint256) {
        require(_nextTokenId < MAX_PLOTS, "All plots minted");
        require(x < 8 && y < 6, "Invalid coordinates");

        uint256 tokenId = _nextTokenId++;
        _safeMint(to, tokenId);

        landPlots[tokenId] = LandPlot({
            x: x,
            y: y,
            fertility: 1000,
            ownedSince: block.timestamp
        });

        emit LandMinted(tokenId, to, x, y);
        return tokenId;
    }

    /**
     * @notice Update land fertility (affected by events)
     */
    function updateFertility(uint256 tokenId, uint16 newFertility) external onlyAuthorized {
        require(newFertility <= 1000, "Fertility exceeds max");
        landPlots[tokenId].fertility = newFertility;
        emit FertilityChanged(tokenId, newFertility);
    }

    /**
     * @notice Get land plot info
     */
    function getLandPlot(uint256 tokenId) external view returns (LandPlot memory) {
        return landPlots[tokenId];
    }

    /**
     * @notice Get all land plots owned by address
     */
    function getLandsByOwner(address owner) external view returns (uint256[] memory) {
        uint256 balance = balanceOf(owner);
        uint256[] memory ownedTokenIds = new uint256[](balance);

        uint256 currentIndex = 0;
        for (uint256 i = 0; i < _nextTokenId; i++) {
            if (_ownerOf(i) == owner) {
                ownedTokenIds[currentIndex] = i;
                currentIndex++;
            }
        }

        return ownedTokenIds;
    }
}

contracts/FarmMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./FarmCoin.sol";
import "./PlantNFT.sol";
import "./CropNFT.sol";
import "./FarmLand.sol";
import "./GameEvents.sol";
import "./GameEffects.sol";
import "./PriceOracle.sol";

/**
 * @title FarmMarketplace
 * @notice Main marketplace for buying seeds, planting, harvesting, and selling crops
 * @dev Orchestrates the entire chain reaction: Buy -> Plant -> Event -> Effect -> Price Change
 */
contract FarmMarketplace is Ownable, ReentrancyGuard {
    FarmCoin public farmCoin;
    PlantNFT public plantNFT;
    CropNFT public cropNFT;
    FarmLand public farmLand;
    GameEvents public gameEvents;
    GameEffects public gameEffects;
    PriceOracle public priceOracle;

    // Shop inventory
    mapping(PriceOracle.SeedType => uint256) public seedInventory;

    event SeedPurchased(address indexed buyer, PriceOracle.SeedType seedType, uint256 quantity, uint256 totalCost);
    event PlantPlanted(address indexed farmer, uint256 plantTokenId, uint256 landTokenId);
    event CropHarvested(address indexed farmer, uint256 plantTokenId, uint256 cropNFTId);
    event CropSold(address indexed seller, uint256 cropTokenId, uint256 reward);
    event EventTriggeredAndApplied(uint256 indexed eventId, address indexed farmer);

    constructor(
        address initialOwner,
        address _farmCoin,
        address _plantNFT,
        address _cropNFT,
        address _farmLand,
        address _gameEvents,
        address _gameEffects,
        address _priceOracle
    ) Ownable(initialOwner) {
        farmCoin = FarmCoin(_farmCoin);
        plantNFT = PlantNFT(_plantNFT);
        cropNFT = CropNFT(_cropNFT);
        farmLand = FarmLand(_farmLand);
        gameEvents = GameEvents(_gameEvents);
        gameEffects = GameEffects(_gameEffects);
        priceOracle = PriceOracle(_priceOracle);

        // Initialize shop inventory (unlimited for demo)
        seedInventory[PriceOracle.SeedType.WHEAT_SEED] = type(uint256).max;
        seedInventory[PriceOracle.SeedType.GRAPE_SEED] = type(uint256).max;
        seedInventory[PriceOracle.SeedType.PUMPKIN_SEED] = type(uint256).max;
    }

    /**
     * @notice Buy seeds from shop using FarmCoin
     */
    function buySeed(PriceOracle.SeedType seedType, uint256 quantity) external nonReentrant {
        require(quantity > 0, "Invalid quantity");
        require(seedInventory[seedType] >= quantity, "Insufficient inventory");

        uint256 pricePerSeed = priceOracle.getSeedPrice(seedType);
        uint256 totalCost = pricePerSeed * quantity;

        // Burn FarmCoin from buyer
        farmCoin.burnFrom(msg.sender, totalCost);

        // Update inventory
        seedInventory[seedType] -= quantity;

        emit SeedPurchased(msg.sender, seedType, quantity, totalCost);

        // TODO: Track seeds in user's inventory (off-chain or separate contract)
    }

    /**
     * @notice Plant a seed on owned land
     */
    function plantSeed(
        PriceOracle.SeedType seedType
    ) external nonReentrant returns (uint256 plantTokenId) {

        PlantNFT.PlantType plantType;
        if (seedType == PriceOracle.SeedType.WHEAT_SEED) {
            plantType = PlantNFT.PlantType.WHEAT;
        } else if (seedType == PriceOracle.SeedType.GRAPE_SEED) {
            plantType = PlantNFT.PlantType.GRAPE;
        } else {
            plantType = PlantNFT.PlantType.PUMPKIN;
        }

        plantTokenId = plantNFT.mint(msg.sender, plantType, 0);

        emit PlantPlanted(msg.sender, plantTokenId, 0);

        return plantTokenId;
    }

    /**
     * @notice Harvest a mature plant and receive Crop NFT
     */
    function harvestCrop(uint256 plantTokenId) external nonReentrant returns (uint256) {
        require(plantNFT.ownerOf(plantTokenId) == msg.sender, "Not plant owner");
        require(plantNFT.isReadyToHarvest(plantTokenId), "Plant not ready");

        PlantNFT.Plant memory plant = plantNFT.getPlant(plantTokenId);

        // Map PlantType to CropType
        CropNFT.CropType cropType;
        if (plant.plantType == PlantNFT.PlantType.WHEAT) {
            cropType = CropNFT.CropType.WHEAT;
        } else if (plant.plantType == PlantNFT.PlantType.GRAPE) {
            cropType = CropNFT.CropType.GRAPE;
        } else {
            cropType = CropNFT.CropType.PUMPKIN;
        }

        // Отмечаем растение как собранное перед сжиганием
        plantNFT.markAsHarvested(plantTokenId);

        // Mint Crop NFT with quality based on plant health
        uint256 cropTokenId = cropNFT.mint(msg.sender, cropType, plant.health);

        // Burn plant NFT (растение превращается в урожай)
        plantNFT.burn(plantTokenId);

        emit CropHarvested(msg.sender, plantTokenId, cropTokenId);

        return cropTokenId;
    }

    /**
     * @notice Sell harvested crop for FarmCoin
     */
    function sellCrop(uint256 cropTokenId) external nonReentrant {
        require(cropNFT.ownerOf(cropTokenId) == msg.sender, "Not crop owner");

        CropNFT.Crop memory crop = cropNFT.getCrop(cropTokenId);

        // Calculate reward based on crop quality
        PriceOracle.CropType oracleCropType;
        if (crop.cropType == CropNFT.CropType.WHEAT) {
            oracleCropType = PriceOracle.CropType.WHEAT;
        } else if (crop.cropType == CropNFT.CropType.GRAPE) {
            oracleCropType = PriceOracle.CropType.GRAPE;
        } else {
            oracleCropType = PriceOracle.CropType.PUMPKIN;
        }

        uint256 basePrice = priceOracle.getCropPrice(oracleCropType);
        uint256 reward = (basePrice * crop.quality) / 1000; // Scale by quality (0-1000)

        // Mint FarmCoin reward
        farmCoin.mint(msg.sender, reward);

        // Burn crop NFT
        cropNFT.burn(cropTokenId);

        emit CropSold(msg.sender, cropTokenId, reward);
    }

    mapping(address => bool) public hasClaimedStarterLand;

    /**
     * @notice Mint initial land plots to a new player (one time only)
     */
    function mintStarterLand() external {
        require(!hasClaimedStarterLand[msg.sender], "Land already claimed");
        hasClaimedStarterLand[msg.sender] = true;

        // Mint 8x6 grid (48 plots)
        for (uint8 y = 0; y < 6; y++) {
            for (uint8 x = 0; x < 8; x++) {
                farmLand.mint(msg.sender, x, y);
            }
        }
    }

    /**
     * @notice Give starter coins to new players
     */
    function giveStarterCoins(address player, uint256 amount) external onlyOwner {
        farmCoin.mint(player, amount);
    }

    /**
     * @notice Emergency withdraw (owner only)
     */
    function emergencyWithdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    /**
     * @notice Get player's farm status
     */
    function getPlayerStatus(address player) external view returns (
        uint256 coinBalance,
        uint256 landCount,
        uint256[] memory landTokenIds
    ) {
        coinBalance = farmCoin.balanceOf(player);
        landTokenIds = farmLand.getLandsByOwner(player);
        landCount = landTokenIds.length;

        return (coinBalance, landCount, landTokenIds);
    }

    receive() external payable {}
}

contracts/GameEffects.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./PlantNFT.sol";

interface IPriceOracle {
    function setWheatMultiplier(uint256 multiplier) external;
}

contract GameEffects is Ownable {
    PlantNFT public plantNFT;
    IPriceOracle public priceOracle;

    mapping(address => uint256) public lastEventProcessed;
    uint256 public constant TIME_REDUCTION = 10 minutes; // Уменьшение времени роста при дожде

    event RainApplied(uint256 plantsAffected, uint256 timeReduced);
    event DroughtApplied(uint256 wheatPriceMultiplier);
    event WinterApplied(address indexed farmer, uint256 plantsDied, uint256 plantsTotal);

    constructor(
        address initialOwner,
        address _plantNFT,
        address _priceOracle
    ) Ownable(initialOwner) {
        plantNFT = PlantNFT(_plantNFT);
        priceOracle = IPriceOracle(_priceOracle);
    }

    function applyEventEffects(uint8 eventType, uint16 severity) external {
        if (eventType == 1) {
            _applyRain();
        } else if (eventType == 2) {
            _applyDrought();
        } else if (eventType == 3) {
            _applyWinter(severity);
        }
    }

    function _applyRain() internal {
        // Дождь автоматически уменьшает время роста всех активных растений
        uint256 totalSupply = plantNFT.totalSupply();
        uint256 affectedCount = 0;

        for (uint256 i = 0; i < totalSupply; i++) {
            uint256 tokenId = plantNFT.tokenByIndex(i);
            PlantNFT.Plant memory plant = plantNFT.getPlant(tokenId);

            // Применяем только к растущим растениям (не собранным)
            if (!plant.isHarvested) {
                plantNFT.reduceGrowthTime(tokenId, TIME_REDUCTION);
                affectedCount++;
            }
        }

        emit RainApplied(affectedCount, TIME_REDUCTION);
    }

    function _applyDrought() internal {
        priceOracle.setWheatMultiplier(2);
        emit DroughtApplied(2);
    }

    function _applyWinter(uint16 severity) internal {
        uint256 killPercent = 10 + (severity * 40 / 1000);
        emit WinterApplied(msg.sender, 0, killPercent);
    }

    // Эта функция больше не нужна - дождь применяется автоматически
    // Оставляем для совместимости, но она ничего не делает
    function applyRainToPlant(uint256 plantTokenId) external view {
        PlantNFT.Plant memory plant = plantNFT.getPlant(plantTokenId);
        require(plant.owner == msg.sender, "Not plant owner");
        // Дождь уже применен автоматически при вызове события
    }

    function applyWinterToFarmer(address farmer) external {
        uint256[] memory plantIds = plantNFT.getPlantsByOwner(farmer);

        if (plantIds.length == 0) return;

        uint256 random = uint256(
            keccak256(abi.encodePacked(block.timestamp, farmer, block.prevrandao))
        );

        uint256 killPercent = 10 + (random % 41);
        uint256 toKill = (plantIds.length * killPercent) / 100;

        for (uint256 i = 0; i < toKill && i < plantIds.length; i++) {
            plantNFT.burn(plantIds[i]);
        }

        emit WinterApplied(farmer, toKill, plantIds.length);
    }
}

contracts/GameEvents.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

contract GameEvents is Ownable {
    enum EventType {
        NONE,
        RAIN,
        DROUGHT,
        WINTER
    }

    struct GameEvent {
        EventType eventType;
        uint16 severity;
        uint256 timestamp;
        uint256 blockNumber;
    }

    GameEvent[] public eventHistory;
    address public effectsContract;
    uint256 public lastEventTime;
    uint256 public constant EVENT_INTERVAL = 2 minutes;

    event EventTriggered(
        uint256 indexed eventId,
        EventType eventType,
        uint16 severity,
        uint256 timestamp
    );

    constructor(address initialOwner) Ownable(initialOwner) {
        lastEventTime = (block.timestamp / EVENT_INTERVAL) * EVENT_INTERVAL;
    }

    function setEffectsContract(address _effectsContract) external onlyOwner {
        effectsContract = _effectsContract;
    }

    function canTriggerEvent() public view returns (bool) {
        uint256 currentRoundedTime = (block.timestamp / EVENT_INTERVAL) * EVENT_INTERVAL;
        return currentRoundedTime > lastEventTime;
    }

    function triggerEvent() external returns (uint256 eventId) {
        require(canTriggerEvent(), "Event already triggered in this interval");

        uint256 currentRoundedTime = (block.timestamp / EVENT_INTERVAL) * EVENT_INTERVAL;
        lastEventTime = currentRoundedTime;

        uint256 random = uint256(
            keccak256(
                abi.encodePacked(
                    currentRoundedTime,
                    block.prevrandao,
                    block.number
                )
            )
        );

        EventType eventType = _selectEventType(random % 100);
        uint16 severity = uint16((random >> 8) % 1001);

        GameEvent memory newEvent = GameEvent({
            eventType: eventType,
            severity: severity,
            timestamp: block.timestamp,
            blockNumber: block.number
        });

        eventHistory.push(newEvent);
        eventId = eventHistory.length - 1;

        emit EventTriggered(eventId, eventType, severity, block.timestamp);

        if (effectsContract != address(0)) {
            (bool success, ) = effectsContract.call(
                abi.encodeWithSignature(
                    "applyEventEffects(uint8,uint16)",
                    uint8(eventType),
                    severity
                )
            );
            require(success, "Effects contract call failed");
        }

        return eventId;
    }

    function _selectEventType(uint256 roll) internal pure returns (EventType) {
        if (roll < 40) return EventType.NONE;
        if (roll < 65) return EventType.RAIN;
        if (roll < 85) return EventType.DROUGHT;
        return EventType.WINTER;
    }

    function getEvent(uint256 eventId) external view returns (GameEvent memory) {
        require(eventId < eventHistory.length, "Event does not exist");
        return eventHistory[eventId];
    }

    function getEventsCount() external view returns (uint256) {
        return eventHistory.length;
    }

    function getCurrentMarketInterval() external view returns (uint256) {
        return (block.timestamp / 5 minutes) * 5 minutes;
    }

    function getMarketIntervalNumber() external view returns (uint256) {
        return block.timestamp / 5 minutes;
    }

    function getRecentEvents(uint256 count) external view returns (GameEvent[] memory) {
        uint256 total = eventHistory.length;
        if (count > total) count = total;

        GameEvent[] memory recent = new GameEvent[](count);
        for (uint256 i = 0; i < count; i++) {
            recent[i] = eventHistory[total - count + i];
        }

        return recent;
    }
}

contracts/PlantNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title PlantNFT
 * @notice ERC721 NFT representing planted crops
 * @dev Each plant has type, growth stage, health, and planted timestamp
 */
contract PlantNFT is ERC721Enumerable, Ownable {
    uint256 private _nextTokenId;

    enum PlantType { WHEAT, GRAPE, PUMPKIN }

    struct Plant {
        PlantType plantType;
        uint8 growthStage;      // 0-3 (seed -> mature)
        uint16 health;          // 0-1000
        uint256 plantedAt;      // timestamp
        uint256 landTokenId;    // which land plot it's on
        bool isHarvested;       // был ли собран урожай
        address owner;          // владелец растения
    }

    mapping(uint256 => Plant) public plants;
    mapping(address => bool) public authorizedManagers;

    event PlantCreated(uint256 indexed tokenId, address indexed owner, PlantType plantType, uint256 landTokenId);
    event PlantGrown(uint256 indexed tokenId, uint8 newStage);
    event PlantHealthChanged(uint256 indexed tokenId, uint16 newHealth);
    event ManagerAuthorized(address indexed manager, bool authorized);

    constructor(address initialOwner) 
        ERC721("Farm Plant", "PLANT") 
        Ownable(initialOwner)
    {}

    modifier onlyAuthorized() {
        require(authorizedManagers[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }

    /**
     * @notice Authorize a contract to manage plants
     */
    function setAuthorizedManager(address manager, bool authorized) external onlyOwner {
        authorizedManagers[manager] = authorized;
        emit ManagerAuthorized(manager, authorized);
    }

    /**
     * @notice Mint a new plant NFT
     */
    function mint(
        address to,
        PlantType plantType,
        uint256 landTokenId
    ) external onlyAuthorized returns (uint256) {
        uint256 tokenId = _nextTokenId++;
        _safeMint(to, tokenId);

        plants[tokenId] = Plant({
            plantType: plantType,
            growthStage: 0,
            health: 1000,
            plantedAt: block.timestamp,
            landTokenId: landTokenId,
            isHarvested: false,
            owner: to
        });

        emit PlantCreated(tokenId, to, plantType, landTokenId);
        return tokenId;
    }

    /**
     * @notice Update plant growth stage
     */
    function updateGrowthStage(uint256 tokenId, uint8 newStage) external onlyAuthorized {
        require(newStage <= 3, "Invalid growth stage");
        plants[tokenId].growthStage = newStage;
        emit PlantGrown(tokenId, newStage);
    }

    /**
     * @notice Update plant health (called by FarmEffects contract)
     */
    function updateHealth(uint256 tokenId, uint16 newHealth) external onlyAuthorized {
        require(newHealth <= 1000, "Health exceeds max");
        plants[tokenId].health = newHealth;
        emit PlantHealthChanged(tokenId, newHealth);
    }

    /**
     * @notice Burn plant NFT (when harvested)
     */
    function burn(uint256 tokenId) external onlyAuthorized {
        _burn(tokenId);
        delete plants[tokenId];
    }

    /**
     * @notice Get plant info
     */
    function getPlant(uint256 tokenId) external view returns (Plant memory) {
        return plants[tokenId];
    }

    /**
     * @notice Check if plant is ready to harvest
     */
    function isReadyToHarvest(uint256 tokenId) external view returns (bool) {
        Plant memory plant = plants[tokenId];

        uint256 requiredTime;
        if (plant.plantType == PlantType.WHEAT) {
            requiredTime = 30;
        } else if (plant.plantType == PlantType.GRAPE) {
            requiredTime = 60;
        } else {
            requiredTime = 45;
        }

        return (block.timestamp >= plant.plantedAt + requiredTime) && 
               (plant.health > 0);
    }

    function reduceGrowthTime(uint256 tokenId, uint256 timeReduction) external onlyAuthorized {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");
        plants[tokenId].plantedAt -= timeReduction;
    }

    function markAsHarvested(uint256 tokenId) external onlyAuthorized {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");
        plants[tokenId].isHarvested = true;
    }

    function getPlantsByOwner(address owner) external view returns (uint256[] memory) {
        uint256 balance = balanceOf(owner);
        uint256[] memory tokenIds = new uint256[](balance);

        for (uint256 i = 0; i < balance; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(owner, i);
        }

        return tokenIds;
    }

    // Override _update для синхронизации owner в структуре Plant
    function _update(address to, uint256 tokenId, address auth) internal virtual override returns (address) {
        address previousOwner = super._update(to, tokenId, auth);

        // Обновляем owner в структуре Plant если токен существует
        if (to != address(0) && plants[tokenId].plantedAt > 0) {
            plants[tokenId].owner = to;
        }

        return previousOwner;
    }
}

contracts/PriceOracle.sol.content:
